{
  "recipes": [
    {
      "name": "Chocolate Chip Cookies",
      "ingredients": ["flour", "sugar", "butter", "chocolate chips"],
      "ingredientQuantity": ["2 cups", "1 cup", "1 cup", "1 1/2 cups"],
      "steps": [
        "Preheat the oven to 350°F.",
        "Cream butter and sugar together.",
        "Mix in flour and chocolate chips.",
        "Scoop dough onto a baking sheet.",
        "Bake for 10-12 minutes."
      ]
    },
    {
      "name": "Chocolate Chip Brownies",
      "ingredients": ["flour", "sugar", "butter", "chocolate chips"],
      "ingredientQuantity": ["1 1/4 cups", "2 cups", "1 cup", "1 1/2 cups"],
      "steps": [
        "Preheat the oven to 350°F.",
        "Melt butter and stir in sugar.",
        "Mix in flour and chocolate chips.",
        "Spread batter into a baking dish.",
        "Bake for 25-30 minutes."
      ]
    },
    {
      "name": "Chocolate Chip Pancakes",
      "ingredients": ["flour", "sugar", "butter", "chocolate chips"],
      "ingredientQuantity": ["1 1/2 cups", "2 tbsp", "4 tbsp", "1/2 cup"],
      "steps": [
        "Combine flour and sugar in a bowl.",
        "Whisk in melted butter and chocolate chips.",
        "Pour batter onto a hot griddle.",
        "Cook until bubbles form, then flip."
      ]
    }
  ]
}
